--Create tables
CREATE TABLE [Release] (
    [ReleaseId]      smallint IDENTITY(1,1)  NOT NULL
  , [ReleaseNumber]  int                     NOT NULL
  , CONSTRAINT [PK_Release_ReleaseId] PRIMARY KEY CLUSTERED ([ReleaseId])
)

CREATE TABLE [Set] (
    [SetId]  smallint IDENTITY(1,1)  NOT NULL
  , [Name]   nvarchar(50)            NOT NULL
  , CONSTRAINT [PK_Set_SetId] PRIMARY KEY CLUSTERED ([SetId])
)

CREATE TABLE [CardType] (
    [CardTypeId]  tinyint IDENTITY(1,1)  NOT NULL
  , [Name]        nvarchar(20)           NOT NULL
  , CONSTRAINT [PK_CardType_CardTypeId] PRIMARY KEY CLUSTERED ([CardTypeId])
)

CREATE TABLE [CardRarity] (
    [CardRarityId]  tinyint IDENTITY(1,1)  NOT NULL
  , [Description]   nvarchar(20)           NOT NULL
  , CONSTRAINT [PK_CardRarity_CardRarityId] PRIMARY KEY CLUSTERED ([CardRarityId])
)

CREATE TABLE [CardSymbol] (
    [CardSymbolId]  tinyint IDENTITY(1,1)  NOT NULL
  , [Name]          nvarchar(10)           NOT NULL
  , CONSTRAINT [PK_CardSymbol_CardSymbolId] PRIMARY KEY CLUSTERED ([CardSymbolId])
)

CREATE TABLE [Zone] (
    [ZoneId]  tinyint IDENTITY(1,1)  NOT NULL
  , [Name]    nvarchar(10)           NOT NULL
  , CONSTRAINT [PK_Zone_ZoneId] PRIMARY KEY CLUSTERED ([ZoneId])
)

CREATE TABLE [Card] (
    [CardId]               int IDENTITY(1,1)  NOT NULL
  , [SetId]                smallint           NOT NULL
  , [Number]               nvarchar(20)       NOT NULL
  , [Name]                 nvarchar(128)      NOT NULL
  , [Description]          nvarchar(1000)     NULL
  , [ReleaseId]            smallint           NOT NULL
  , [CardTypeId]           tinyint            NOT NULL
  , [CardRarityId]         tinyint            NOT NULL
  , [Check]                tinyint            NOT NULL
  , [Difficulty]           tinyint            NOT NULL
  , [BlockModifier]        tinyint            NOT NULL
  , [BlockZoneId]          tinyint            NOT NULL
  , [AttackSpeed]          tinyint            NULL
  , [AttackDamage]         tinyint            NULL
  , [AttackZoneId]         tinyint            NULL
  , [HandSize]             tinyint            NULL
  , [Vitality]             tinyint            NULL
  , [ImageUrl]             nvarchar(2000)     NULL
  , [TcgUrl]               nvarchar(2000)     NULL
  , CONSTRAINT [PK_Card_CardId] PRIMARY KEY CLUSTERED ([CardId])
  , CONSTRAINT [FK_Card_ReleaseId] FOREIGN KEY (ReleaseId) REFERENCES [Release]([ReleaseId])
  , CONSTRAINT [FK_Card_SetId] FOREIGN KEY (SetId) REFERENCES [Set]([SetId])
  , CONSTRAINT [FK_Card_CardTypeId] FOREIGN KEY (CardTypeId) REFERENCES [CardType]([CardTypeId])
  , CONSTRAINT [FK_Card_CardRarityId] FOREIGN KEY (CardRarityId) REFERENCES [CardRarity]([CardRarityId])
  , CONSTRAINT [FK_Card_BlockZoneId] FOREIGN KEY (BlockZoneId) REFERENCES [Zone]([ZoneId])
  , CONSTRAINT [FK_Card_AttackZoneId] FOREIGN KEY (AttackZoneId) REFERENCES [Zone]([ZoneId])
)

--Create relation tables
CREATE TABLE [CardResource] (
    [CardResourceId]  int IDENTITY(1,1)  NOT NULL
  , [CardId]          int                NOT NULL
  , [CardSymbolId]    tinyint            NOT NULL
  , CONSTRAINT [PK_CardResource_CardResourceId] PRIMARY KEY CLUSTERED (CardResourceId)
  , CONSTRAINT [FK_CardResource_CardId] FOREIGN KEY (CardId) REFERENCES Card(CardId)
  , CONSTRAINT [FK_CardResource_CardSymbolId] FOREIGN KEY (CardSymbolId) REFERENCES CardSymbol(CardSymbolId)
)

CREATE TABLE [CardKeyword] (
    [CardKeywordId]  int IDENTITY(1,1)  NOT NULL
  , [CardId]         int                NOT NULL
  , [Keyword]        nvarchar(100)      NOT NULL
  , CONSTRAINT [PK_CardKeyword_CardKeywordId] PRIMARY KEY CLUSTERED ([CardKeywordId])
  , CONSTRAINT [FK_CardKeyword_CardId] FOREIGN KEY ([CardId]) REFERENCES [Card]([CardId])
)


