--Get the cards
SELECT
	  [Card].[CardId]
	, [Set].[Name] AS [SetName]
	, [Card].[Number]
	, [Card].[Name]
	, [Card].[Description]
	, [Release].[ReleaseNumber] AS [ReleaseNumber]
	, STUFF((
		SELECT
			COALESCE(', '+[CardSymbol].[Name], '')
		FROM dbo.[CardResource] [CardResource2]
		INNER JOIN dbo.[CardSymbol]
			ON [CardSymbol].[CardSymbolId] = [CardResource2].[CardSymbolId]
		WHERE [CardResource2].CardId = [Card].CardId
		FOR XML PATH('')
		)
		, 1, 2, ''
	) AS [CardResources]
	, [CardType].[Name] AS [CardType]
	, [CardRarity].[Description] AS [CardRarity]
	, [Card].[Check]
	, [Card].[Difficulty]
	, [Card].[BlockModifier]
	, [BlockZone].[Name] AS [BlockZone]
	, [Card].[AttackSpeed]
	, [Card].[AttackDamage]
	, [AttackZone].[Name] AS [AttackZone]
	, [Card].[HandSize]
	, [Card].[Vitality]
	, STUFF((
		SELECT
			COALESCE(', '+[CardKeyword2].[Keyword], '')
		FROM dbo.[CardKeyword] [CardKeyword2]
		WHERE [CardKeyword2].CardId = [Card].CardId
		FOR XML PATH('')
		)
		, 1, 2, ''
	) AS [CardKeywords]
	, [Card].[ImageUrl]
	, [Card].[TcgUrl]
FROM dbo.[Card]
LEFT OUTER JOIN dbo.[Release]
	ON [Release].[ReleaseId] = [Card].[ReleaseId]
LEFT OUTER JOIN dbo.[Set]
	ON [Set].[SetId] = [Card].[SetId]
LEFT OUTER JOIN dbo.[CardType]
	ON [CardType].[CardTypeId] = [Card].[CardTypeId]
LEFT OUTER JOIN dbo.[CardRarity]
	ON [CardRarity].[CardRarityId] = [Card].[CardRarityId]
LEFT OUTER JOIN dbo.[Zone] [BlockZone]
	ON [BlockZone].[ZoneId] = [Card].[BlockZoneId]
LEFT OUTER JOIN dbo.[Zone] [AttackZone]
	ON [AttackZone].[ZoneId] = [Card].[AttackZoneId]
ORDER BY 1
